mapper.update:
UPDATE 	bragging_board
SET		title = #{title},   content = #{content}, update_Date = SYSDATE
WHERE	bno = #{bno}

mapper.select:
SELECT COUNT(*)
FROM bragging_recommend
WHERE bno = #{bno} AND id = #{id}

mapper.update:
UPDATE bragging_board 
SET recommendCnt = recommendCnt + 1
WHERE bno = #{bno}
